import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { User, Mail, Save, Shield, Bell, Moon, Sun } from 'lucide-react';
import toast from 'react-hot-toast';

const Settings = () => {
    const { user } = useAuth();
    const [formData, setFormData] = useState({
        username: user?.username || '',
        email: user?.email || '',
    });
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (user) {
            setFormData({
                username: user.username || '',
                email: user.email || '',
            });
        }
    }, [user]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const res = await api.put('/auth/profile', formData);
            // Update local storage user data
            const updatedUser = { ...user, ...formData };
            localStorage.setItem('user', JSON.stringify(updatedUser));
            toast.success('Profile updated successfully!');
            // Note: In a real app, you might want to force a refresh or update context state
        } catch (error) {
            toast.error('Failed to update profile');
        } finally {
            setLoading(false);
        }
    };

    const sectionStyle = {
        backgroundColor: 'var(--bg-card)',
        borderRadius: '16px',
        padding: '24px',
        marginBottom: '24px',
        border: '1px solid var(--border)'
    };

    const labelStyle = {
        display: 'block',
        marginBottom: '8px',
        fontWeight: '600',
        fontSize: '14px',
        color: 'var(--text-main)'
    };

    return (
        <Layout>
            <div style={{ maxWidth: '800px', margin: '0 auto' }}>
                <div style={{ marginBottom: '40px' }}>
                    <h2 style={{ fontSize: '28px', fontWeight: '800', marginBottom: '8px' }}>Settings</h2>
                    <p style={{ color: 'var(--text-muted)' }}>Manage your account preferences and profile.</p>
                </div>

                <section style={sectionStyle}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '24px' }}>
                        <div style={{ padding: '10px', backgroundColor: 'rgba(59, 130, 246, 0.1)', borderRadius: '12px', color: 'var(--primary)' }}>
                            <User size={20} />
                        </div>
                        <h3 style={{ fontSize: '18px', fontWeight: '700' }}>Profile Information</h3>
                    </div>

                    <form onSubmit={handleSubmit}>
                        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
                            <div>
                                <label style={labelStyle}>Username</label>
                                <div style={{ position: 'relative' }}>
                                    <User size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: 'var(--text-muted)' }} />
                                    <input
                                        type="text"
                                        style={{ width: '100%', paddingLeft: '40px' }}
                                        value={formData.username}
                                        onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                                        required
                                    />
                                </div>
                            </div>
                            <div>
                                <label style={labelStyle}>Email Address</label>
                                <div style={{ position: 'relative' }}>
                                    <Mail size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: 'var(--text-muted)' }} />
                                    <input
                                        type="email"
                                        style={{ width: '100%', paddingLeft: '40px' }}
                                        value={formData.email}
                                        onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                        required
                                    />
                                </div>
                            </div>
                        </div>
                        <button type="submit" className="btn-primary" disabled={loading} style={{ display: 'flex', alignItems: 'center', gap: '8px', padding: '12px 24px' }}>
                            <Save size={18} /> {loading ? 'Saving...' : 'Save Changes'}
                        </button>
                    </form>
                </section>

                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '24px' }}>
                    <section style={sectionStyle}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '15px' }}>
                            <Shield size={20} color="var(--primary)" />
                            <h3 style={{ fontSize: '16px', fontWeight: '700' }}>Security</h3>
                        </div>
                        <p style={{ fontSize: '13px', color: 'var(--text-muted)', marginBottom: '15px' }}>Update your password to keep your account secure.</p>

                        <form onSubmit={async (e) => {
                            e.preventDefault();
                            const newPassword = e.target.password.value;
                            const confirmPassword = e.target.confirmPassword.value;

                            if (newPassword !== confirmPassword) {
                                return toast.error('Passwords do not match');
                            }

                            setLoading(true);
                            try {
                                await api.put('/auth/profile', { password: newPassword });
                                toast.success('Password updated successfully!');
                                e.target.reset();
                            } catch (error) {
                                toast.error('Failed to update password');
                            } finally {
                                setLoading(false);
                            }
                        }}>
                            <div style={{ marginBottom: '12px' }}>
                                <label style={labelStyle}>New Password</label>
                                <input name="password" type="password" style={{ width: '100%' }} required />
                            </div>
                            <div style={{ marginBottom: '15px' }}>
                                <label style={labelStyle}>Confirm New Password</label>
                                <input name="confirmPassword" type="password" style={{ width: '100%' }} required />
                            </div>
                            <button type="submit" className="btn-secondary" style={{ width: '100%' }}>Change Password</button>
                        </form>
                    </section>

                    <section style={sectionStyle}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '15px' }}>
                            <Bell size={20} color="var(--primary)" />
                            <h3 style={{ fontSize: '16px', fontWeight: '700' }}>Notifications</h3>
                        </div>
                        <p style={{ fontSize: '13px', color: 'var(--text-muted)', marginBottom: '15px' }}>Email reminders are currently enabled for all your tasks.</p>
                        <button
                            onClick={() => toast.success('Notification settings saved!')}
                            className="btn-secondary"
                            style={{ width: '100%' }}
                        >
                            Manage Alerts
                        </button>
                    </section>
                </div>
            </div>
        </Layout>
    );
};

export default Settings;
