import axios from 'axios';

const api = axios.create({
    baseURL: 'http://192.168.1.6:5000',
});

// Mock Database for Guest Mode
const getMockDB = () => JSON.parse(localStorage.getItem('guest_db')) || { timetable: [], notifications: [] };
const saveMockDB = (db) => localStorage.setItem('guest_db', JSON.stringify(db));

// Helper to delay response for realism
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// Add a request interceptor
api.interceptors.request.use(
    async (config) => {
        const token = localStorage.getItem('token');

        // --- OFFLINE / GUEST MODE HANDLING ---
        if (token === 'guest-token') {
            console.log(`[OFFLINE MODE] Intercepting ${config.method.toUpperCase()} ${config.url}`);

            // Wait 500ms to simulate network
            await delay(500);

            const db = getMockDB();
            const adapter = new axios.AxiosAdapter(); // Not used directly, implementing custom adapter logic

            // 1. TIMETABLE ROUTES
            if (config.url === '/timetable' && config.method === 'get') {
                config.adapter = () => Promise.resolve({ data: db.timetable, status: 200, statusText: 'OK', headers: {}, config });
            }

            else if (config.url === '/timetable' && config.method === 'post') {
                const newEntry = { id: Date.now(), ...JSON.parse(config.data), is_completed: false };
                db.timetable.push(newEntry);
                saveMockDB(db);
                config.adapter = () => Promise.resolve({ data: newEntry, status: 201, statusText: 'Created', headers: {}, config });
            }

            else if (config.url.startsWith('/timetable/') && config.method === 'delete') {
                const id = parseInt(config.url.split('/').pop());
                db.timetable = db.timetable.filter(t => t.id !== id);
                saveMockDB(db);
                config.adapter = () => Promise.resolve({ data: { message: 'Deleted' }, status: 200, statusText: 'OK', headers: {}, config });
            }

            else if (config.url.startsWith('/timetable/') && config.method === 'put') {
                const id = parseInt(config.url.split('/').pop());
                const updates = JSON.parse(config.data);
                db.timetable = db.timetable.map(t => t.id === id ? { ...t, ...updates } : t);
                saveMockDB(db);
                config.adapter = () => Promise.resolve({ data: { message: 'Updated' }, status: 200, statusText: 'OK', headers: {}, config });
            }

            // 2. NOTIFICATION ROUTES
            else if (config.url === '/notifications' && config.method === 'get') {
                const mockNotifs = [
                    { id: 1, message: 'Welcome to Offline Mode!', is_read: false },
                    { id: 2, message: 'Your data is saved locally.', is_read: false }
                ];
                config.adapter = () => Promise.resolve({ data: mockNotifs, status: 200, statusText: 'OK', headers: {}, config });
            }
            else if (config.url.includes('/notifications') && config.method === 'put') {
                config.adapter = () => Promise.resolve({ data: { success: true }, status: 200, statusText: 'OK', headers: {}, config });
            }

            // 3. PING
            else if (config.url === '/api/ping') {
                config.adapter = () => Promise.resolve({ data: { message: 'Offline Mode Active' }, status: 200, statusText: 'OK', headers: {}, config });
            }

            return config;
        }

        // --- STANDARD ONLINE MODE ---
        const fullUrl = `${config.baseURL}${config.url}`;
        console.log(`>>> [${new Date().toLocaleTimeString()}] API CALL: ${config.method.toUpperCase()} ${fullUrl}`);

        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

export default api;
