import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Layout from '../components/Layout';
import Modal from '../components/Modal';
import api from '../services/api';
import {
    Plus,
    Search,
    Clock,
    Edit2,
    Trash2,
    Loader2,
    Calendar as CalendarIcon,
    CheckCircle
} from 'lucide-react';
import toast from 'react-hot-toast';

const Timetable = () => {
    const [timetable, setTimetable] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingEntry, setEditingEntry] = useState(null);
    const [filterDay, setFilterDay] = useState('All');
    const [searchTerm, setSearchTerm] = useState('');
    const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth < 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const handleToggleComplete = async (entry) => {
        try {
            await api.put(`/timetable/${entry.id}`, {
                ...entry,
                is_completed: !entry.is_completed,
                start_time: entry.start_time.substring(0, 5),
                end_time: entry.end_time.substring(0, 5),
                repeat_config: typeof entry.repeat_config === 'string' ? JSON.parse(entry.repeat_config) : entry.repeat_config
            });
            toast.success(entry.is_completed ? 'Task marked as pending' : 'Task marked as completed');
            fetchData();
        } catch (error) {
            toast.error('Failed to update task');
        }
    };

    const [formData, setFormData] = useState({
        subject: '',
        day: 'Monday',
        start_time: '',
        end_time: '',
        location: '',
        priority: 'Medium',
        notes: '',
        repeat_config: { type: 'None' }
    });

    const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

    const location = useLocation();
    const { state } = location;

    const fetchData = async () => {
        try {
            setLoading(true);
            const res = await api.get('/timetable');
            setTimetable(res.data);

            // If there's an editId in state, find the entry and open modal
            if (state?.editId) {
                const entryToEdit = res.data.find(item => item.id === state.editId);
                if (entryToEdit) {
                    handleOpenModal(entryToEdit);
                }
            }
        } catch (error) {
            toast.error('Failed to load timetable');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, [state]);

    const handleOpenModal = (entry = null) => {
        if (entry) {
            setEditingEntry(entry);
            const repeatConfig = typeof entry.repeat_config === 'string' ? JSON.parse(entry.repeat_config) : (entry.repeat_config || { type: 'None' });
            setFormData({
                ...entry,
                start_time: entry.start_time.substring(0, 5),
                end_time: entry.end_time.substring(0, 5),
                repeat_config: repeatConfig,
                alert_offset: entry.alert_offset || 0
            });
        } else {
            setEditingEntry(null);
            setFormData({
                subject: '',
                day: 'Monday',
                start_time: '',
                end_time: '',
                location: '',
                priority: 'Medium',
                notes: '',
                repeat_config: { type: 'None' },
                alert_offset: 0
            });
        }
        setIsModalOpen(true);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Basic validation check
        if (!formData.subject || !formData.start_time || !formData.end_time) {
            toast.error('Please fill in all required fields');
            return;
        }

        try {
            if (editingEntry) {
                await api.put(`/timetable/${editingEntry.id}`, formData);
                toast.success('Entry updated');
            } else {
                await api.post('/timetable', formData);
                toast.success('Entry added');
            }
            setIsModalOpen(false);
            fetchData();
        } catch (error) {
            const errMsg = error.response?.data?.message || error.message;
            toast.error(`Failed to save: ${errMsg}`);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this entry?')) {
            try {
                await api.delete(`/timetable/${id}`);
                toast.success('Entry deleted');
                fetchData();
            } catch (error) {
                toast.error('Failed to delete entry');
            }
        }
    };

    const filteredTimetable = timetable.filter(item => {
        const repeatConfig = typeof item.repeat_config === 'string' ? JSON.parse(item.repeat_config) : (item.repeat_config || { type: 'None' });
        const matchesDay = filterDay === 'All' || item.day === filterDay || repeatConfig.type === 'Daily';
        const matchesSearch = item.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
            item.location.toLowerCase().includes(searchTerm.toLowerCase());
        return matchesDay && matchesSearch;
    });

    return (
        <Layout>
            <div style={{
                display: 'flex',
                flexDirection: isMobile ? 'column' : 'row',
                justifyContent: 'space-between',
                alignItems: isMobile ? 'stretch' : 'flex-start',
                marginBottom: '30px',
                gap: '20px'
            }}>
                <div>
                    <h2 style={{ fontSize: '28px', fontWeight: '800', marginBottom: '8px' }}>Manage Timetable</h2>
                    <p style={{ color: 'var(--text-muted)' }}>Customize your weekly schedule and tasks.</p>
                </div>
                <div style={{ display: 'flex', gap: '15px', flexDirection: isMobile ? 'column' : 'row' }}>
                    <div style={{ position: 'relative', flex: 1 }}>
                        <Search size={18} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: 'var(--text-muted)' }} />
                        <input
                            type="text"
                            placeholder="Search subjects..."
                            style={{ paddingLeft: '40px', width: '100%', minWidth: isMobile ? '0' : '200px' }}
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <button
                        onClick={() => handleOpenModal()}
                        className="btn-primary"
                        style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px', padding: '12px 24px', whiteSpace: 'nowrap' }}
                    >
                        <Plus size={20} /> Add Entry
                    </button>
                </div>
            </div>

            <div style={{
                display: 'flex',
                gap: '12px',
                marginBottom: '30px',
                overflowX: 'auto',
                paddingBottom: '10px',
                scrollbarWidth: 'none', /* Firefox */
                msOverflowStyle: 'none' /* IE/Edge */
            }}>
                <style>{`
                    /* Hide scrollbar for Chrome, Safari and Opera */
                    div::-webkit-scrollbar {
                        display: none;
                    }
                `}</style>
                <button
                    onClick={() => setFilterDay('All')}
                    style={{
                        padding: '8px 16px',
                        borderRadius: '20px',
                        backgroundColor: filterDay === 'All' ? 'var(--primary)' : 'var(--bg-card)',
                        color: filterDay === 'All' ? 'white' : 'var(--text-main)',
                        fontWeight: '600',
                        fontSize: '13px',
                        whiteSpace: 'nowrap',
                        border: '1px solid var(--border)',
                        flexShrink: 0
                    }}
                >
                    All Days
                </button>
                {days.map(day => (
                    <button
                        key={day}
                        onClick={() => setFilterDay(day)}
                        style={{
                            padding: '8px 16px',
                            borderRadius: '20px',
                            backgroundColor: filterDay === day ? 'var(--primary)' : 'var(--bg-card)',
                            color: filterDay === day ? 'white' : 'var(--text-main)',
                            fontWeight: '600',
                            fontSize: '13px',
                            whiteSpace: 'nowrap',
                            border: '1px solid var(--border)',
                            flexShrink: 0
                        }}
                    >
                        {day}
                    </button>
                ))}
            </div>

            {loading ? (
                <div style={{ display: 'flex', justifyContent: 'center', padding: '100px' }}>
                    <Loader2 className="animate-spin" size={40} color="var(--primary)" />
                </div>
            ) : (
                <div style={{ display: 'grid', gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fill, minmax(260px, 1fr))', gap: '16px' }}>
                    {filteredTimetable.length === 0 ? (
                        <div style={{ gridColumn: '1/-1', textAlign: 'center', padding: '60px', color: 'var(--text-muted)' }}>
                            <CalendarIcon size={48} style={{ marginBottom: '15px', opacity: 0.3 }} />
                            <p>No entries found for {filterDay === 'All' ? 'any day' : filterDay}.</p>
                        </div>
                    ) : (
                        filteredTimetable.map(item => (
                            <div key={item.id} className="card" style={{ opacity: item.is_completed ? 0.6 : 1 }}>
                                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
                                    <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                                        <div style={{
                                            padding: '4px 10px',
                                            borderRadius: '8px',
                                            fontSize: '12px',
                                            fontWeight: '700',
                                            backgroundColor: 'rgba(59, 130, 246, 0.1)',
                                            color: 'var(--primary)'
                                        }}>
                                            {item.day}
                                        </div>
                                        <button
                                            onClick={() => handleToggleComplete(item)}
                                            style={{ background: 'none', color: item.is_completed ? '#10b981' : 'var(--text-muted)' }}
                                        >
                                            <CheckCircle size={18} fill={item.is_completed ? '#10b981' : 'transparent'} color={item.is_completed ? 'white' : 'currentColor'} />
                                        </button>
                                    </div>
                                    <div style={{ display: 'flex', gap: '10px' }}>
                                        <button onClick={() => handleOpenModal(item)} style={{ background: 'none', color: 'var(--text-muted)' }}><Edit2 size={16} /></button>
                                        <button onClick={() => handleDelete(item.id)} style={{ background: 'none', color: '#ef4444' }}><Trash2 size={16} /></button>
                                    </div>
                                </div>

                                <h3 style={{ fontSize: '18px', fontWeight: '800', marginBottom: '5px', textDecoration: item.is_completed ? 'line-through' : 'none' }}>{item.subject}</h3>
                                <div style={{ display: 'flex', alignItems: 'center', gap: '8px', color: 'var(--text-muted)', fontSize: '14px', marginBottom: '15px' }}>
                                    <Clock size={14} />
                                    {item.start_time.substring(0, 5)} - {item.end_time.substring(0, 5)}
                                </div>

                                {item.notes && (
                                    <p style={{
                                        fontSize: '13px',
                                        color: 'var(--text-muted)',
                                        marginBottom: '15px',
                                        padding: '10px',
                                        backgroundColor: 'var(--bg-main)',
                                        borderRadius: '8px',
                                        fontStyle: 'italic'
                                    }}>
                                        "{item.notes}"
                                    </p>
                                )}

                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                    <span style={{ fontSize: '13px', color: 'var(--text-muted)' }}>{item.location}</span>
                                    <span style={{
                                        fontSize: '11px',
                                        fontWeight: '800',
                                        textTransform: 'uppercase',
                                        color: item.priority === 'High' ? '#ef4444' : item.priority === 'Medium' ? '#f59e0b' : '#10b981'
                                    }}>
                                        {item.priority} Priority
                                    </span>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            )}

            {/* Entry Modal */}
            <Modal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                title={editingEntry ? 'Edit Entry' : 'Add New Entry'}
            >
                <form onSubmit={handleSubmit}>
                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Subject Name</label>
                        <input
                            type="text"
                            style={{ width: '100%' }}
                            value={formData.subject}
                            onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                            required
                        />
                    </div>

                    <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Day</label>
                            <select
                                style={{ width: '100%' }}
                                value={formData.day}
                                onChange={(e) => setFormData({ ...formData, day: e.target.value })}
                            >
                                {days.map(d => <option key={d} value={d}>{d}</option>)}
                            </select>
                        </div>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Priority</label>
                            <select
                                style={{ width: '100%' }}
                                value={formData.priority}
                                onChange={(e) => setFormData({ ...formData, priority: e.target.value })}
                            >
                                <option value="Low">Low</option>
                                <option value="Medium">Medium</option>
                                <option value="High">High</option>
                            </select>
                        </div>
                    </div>

                    <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Repeat Schedule</label>
                            <select
                                style={{ width: '100%' }}
                                value={formData.repeat_config?.type || 'None'}
                                onChange={(e) => setFormData({ ...formData, repeat_config: { type: e.target.value } })}
                            >
                                <option value="None">No Repeat</option>
                                <option value="Daily">Daily</option>
                            </select>
                        </div>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Alert Before</label>
                            <select
                                style={{ width: '100%' }}
                                value={formData.alert_offset}
                                onChange={(e) => setFormData({ ...formData, alert_offset: parseInt(e.target.value) })}
                            >
                                <option value={0}>No Alert</option>
                                <option value={5}>5 Minutes</option>
                                <option value={10}>10 Minutes</option>
                                <option value={30}>30 Minutes</option>
                                <option value={60}>1 Hour</option>
                            </select>
                        </div>
                    </div>

                    <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Start Time</label>
                            <input
                                type="time"
                                style={{ width: '100%' }}
                                value={formData.start_time}
                                onChange={(e) => setFormData({ ...formData, start_time: e.target.value })}
                                required
                            />
                        </div>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>End Time</label>
                            <input
                                type="time"
                                style={{ width: '100%' }}
                                value={formData.end_time}
                                onChange={(e) => setFormData({ ...formData, end_time: e.target.value })}
                                required
                            />
                        </div>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Location</label>
                        <input
                            type="text"
                            style={{ width: '100%' }}
                            value={formData.location}
                            onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                        />
                    </div>

                    <div style={{ marginBottom: '25px' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Notes / Additional Info</label>
                        <textarea
                            style={{ width: '100%', height: '80px', padding: '12px', resize: 'none' }}
                            placeholder="Add any specific details about this session..."
                            value={formData.notes || ''}
                            onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                        ></textarea>
                    </div>

                    <button type="submit" className="btn-primary" style={{ width: '100%', padding: '12px' }}>
                        {editingEntry ? 'Update Entry' : 'Create Entry'}
                    </button>
                </form>
            </Modal>

            <style>{`
    .animate-spin {
      animation: spin 1s linear infinite;
    }
    @keyframes spin {
      from { transform: rotate(0deg); }
      to { transform: rotate(360deg); }
    }
  `}</style>
        </Layout>
    );
};

export default Timetable;
