import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import api from '../services/api';
import {
    Activity,
    CheckCircle,
    Clock,
    AlertCircle,
    TrendingUp,
    MoreVertical,
    Plus,
    Trash2,
    Edit2
} from 'lucide-react';
import toast from 'react-hot-toast';

const Dashboard = () => {
    const navigate = useNavigate();
    const [timetable, setTimetable] = useState([]);
    const [loading, setLoading] = useState(true);
    const [activeMenu, setActiveMenu] = useState(null);

    const fetchData = async () => {
        try {
            setLoading(true);
            const res = await api.get('/timetable');
            setTimetable(res.data);
        } catch (error) {
            toast.error('Failed to load timetable');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const handleDeleteTask = async (id) => {
        if (window.confirm('Are you sure you want to delete this task?')) {
            try {
                await api.delete(`/timetable/${id}`);
                toast.success('Task deleted');
                fetchData();
            } catch (error) {
                toast.error('Failed to delete task');
            }
        }
    };

    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const today = days[new Date().getDay()];

    const totalTasks = timetable.length;
    const completedCount = timetable.filter(entry => entry.is_completed).length;
    const pendingCount = totalTasks - completedCount;
    const completionRate = totalTasks > 0 ? Math.round((completedCount / totalTasks) * 100) : 0;

    const todayEntries = timetable.filter(entry => {
        const repeatConfig = typeof entry.repeat_config === 'string' ? JSON.parse(entry.repeat_config) : (entry.repeat_config || { type: 'None' });
        return entry.day === today || repeatConfig.type === 'Daily';
    });

    const stats = [
        { label: 'Total Tasks', value: totalTasks, icon: <Activity size={20} />, color: 'var(--primary)' },
        { label: 'Completed', value: completedCount, icon: <CheckCircle size={20} />, color: '#10b981' },
        { label: 'Pending', value: pendingCount, icon: <Clock size={20} />, color: '#f59e0b' },
        { label: 'Productivity', value: `${completionRate}%`, icon: <TrendingUp size={20} />, color: '#8b5cf6' },
    ];

    const handleToggleComplete = async (entry) => {
        try {
            await api.put(`/timetable/${entry.id}`, {
                ...entry,
                is_completed: !entry.is_completed,
                start_time: entry.start_time.substring(0, 5),
                end_time: entry.end_time.substring(0, 5),
                repeat_config: typeof entry.repeat_config === 'string' ? JSON.parse(entry.repeat_config) : entry.repeat_config
            });
            toast.success(entry.is_completed ? 'Task marked as pending' : 'Task marked as completed');
            fetchData();
        } catch (error) {
            toast.error('Failed to update task');
        }
    };

    return (
        <Layout>
            <div style={{ marginBottom: '30px', display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                <div>
                    <h2 style={{ fontSize: '28px', fontWeight: '800', marginBottom: '8px' }}>Dashboard Overview</h2>
                    <p style={{ color: 'var(--text-muted)' }}>Welcome back! Here's what's happening today.</p>
                </div>
                <button
                    onClick={() => navigate('/timetable')}
                    className="btn-primary"
                    style={{ display: 'flex', alignItems: 'center', gap: '8px', padding: '12px 20px' }}
                >
                    <Plus size={20} /> Add Entry
                </button>
            </div>

            {/* Stats Grid */}
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))',
                gap: '24px',
                marginBottom: '40px'
            }}>
                {stats.map((stat, i) => (
                    <div key={i} className="card" style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
                        <div style={{
                            width: '50px',
                            height: '50px',
                            borderRadius: '12px',
                            backgroundColor: `${stat.color}15`,
                            color: stat.color,
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center'
                        }}>
                            {stat.icon}
                        </div>
                        <div>
                            <div style={{ fontSize: '14px', color: 'var(--text-muted)', fontWeight: '600' }}>{stat.label}</div>
                            <div style={{ fontSize: '24px', fontWeight: '800' }}>{stat.value}</div>
                        </div>
                    </div>
                ))}
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '30px' }}>
                {/* Today's Schedule */}
                <div className="card">
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '25px' }}>
                        <h3 style={{ fontSize: '18px', fontWeight: '700' }}>Today's Schedule</h3>
                        <span style={{ fontSize: '14px', color: 'var(--primary)', fontWeight: '600' }}>{today}</span>
                    </div>

                    {loading ? (
                        <p>Loading...</p>
                    ) : todayEntries.length === 0 ? (
                        <div style={{ textAlign: 'center', padding: '40px', color: 'var(--text-muted)' }}>
                            No classes scheduled for today.
                        </div>
                    ) : (
                        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
                            {todayEntries.map((entry) => (
                                <div key={entry.id} style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '20px',
                                    padding: '16px',
                                    borderRadius: '16px',
                                    backgroundColor: 'var(--bg-main)',
                                    borderLeft: `5px solid ${entry.color || 'var(--primary)'}`,
                                    position: 'relative'
                                }}>
                                    <div style={{ fontWeight: '700', minWidth: '80px', fontSize: '14px' }}>
                                        {entry.start_time.substring(0, 5)}
                                    </div>
                                    <button
                                        onClick={() => handleToggleComplete(entry)}
                                        style={{
                                            background: 'none',
                                            color: entry.is_completed ? '#10b981' : 'var(--text-muted)',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center'
                                        }}
                                    >
                                        <CheckCircle size={22} fill={entry.is_completed ? '#10b981' : 'transparent'} color={entry.is_completed ? 'white' : 'currentColor'} />
                                    </button>
                                    <div style={{ flex: 1, opacity: entry.is_completed ? 0.6 : 1 }}>
                                        <div style={{ fontWeight: '700', textDecoration: entry.is_completed ? 'line-through' : 'none' }}>{entry.subject}</div>
                                        <div style={{ fontSize: '13px', color: 'var(--text-muted)' }}>{entry.location}</div>
                                    </div>
                                    <div style={{
                                        padding: '4px 12px',
                                        borderRadius: '20px',
                                        fontSize: '12px',
                                        fontWeight: '700',
                                        backgroundColor: entry.priority === 'High' ? '#fee2e2' : entry.priority === 'Medium' ? '#fef3c7' : '#dcfce7',
                                        color: entry.priority === 'High' ? '#ef4444' : entry.priority === 'Medium' ? '#f59e0b' : '#10b981'
                                    }}>
                                        {entry.priority}
                                    </div>
                                    <div style={{ position: 'relative' }}>
                                        <button
                                            onClick={() => setActiveMenu(activeMenu === entry.id ? null : entry.id)}
                                            style={{ background: 'none', color: 'var(--text-muted)' }}
                                        >
                                            <MoreVertical size={18} />
                                        </button>
                                        {activeMenu === entry.id && (
                                            <div className="card" style={{
                                                position: 'absolute',
                                                top: '30px',
                                                right: 0,
                                                zIndex: 10,
                                                padding: '8px',
                                                width: '120px',
                                                boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
                                            }}>
                                                <button
                                                    onClick={() => navigate('/timetable', { state: { editId: entry.id } })}
                                                    style={{ display: 'flex', alignItems: 'center', gap: '8px', width: '100%', padding: '8px', background: 'none', fontSize: '13px' }}
                                                >
                                                    <Edit2 size={14} /> Edit
                                                </button>
                                                <button
                                                    onClick={() => handleDeleteTask(entry.id)}
                                                    style={{ display: 'flex', alignItems: 'center', gap: '8px', width: '100%', padding: '8px', background: 'none', fontSize: '13px', color: '#ef4444' }}
                                                >
                                                    <Trash2 size={14} /> Delete
                                                </button>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                {/* Upcoming Tasks / Quick Actions */}
                <div style={{ display: 'flex', flexDirection: 'column', gap: '30px' }}>
                    <div className="card" style={{ background: 'linear-gradient(135deg, var(--primary) 0%, #1e3a8a 100%)', color: 'white' }}>
                        <h3 style={{ fontSize: '18px', fontWeight: '700', marginBottom: '15px' }}>Quick Start</h3>
                        <p style={{ fontSize: '14px', opacity: 0.8, marginBottom: '20px' }}>Create a new timetable entry for your classes or tasks.</p>
                        <button
                            onClick={() => navigate('/timetable')}
                            className="btn-primary"
                            style={{ backgroundColor: 'white', color: 'var(--primary)', width: '100%', padding: '12px' }}
                        >
                            Add New Entry
                        </button>
                    </div>

                    <div className="card">
                        <h3 style={{ fontSize: '18px', fontWeight: '700', marginBottom: '20px' }}>Weekly Progress</h3>
                        <div style={{ height: '8px', backgroundColor: 'var(--bg-main)', borderRadius: '4px', overflow: 'hidden', marginBottom: '10px' }}>
                            <div style={{
                                width: `${timetable.length ? (completedCount / timetable.length) * 100 : 0}%`,
                                height: '100%',
                                backgroundColor: 'var(--primary)',
                                transition: 'width 0.5s ease'
                            }}></div>
                        </div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '13px', color: 'var(--text-muted)' }}>
                            <span>Completion Rate</span>
                            <span style={{ fontWeight: '700', color: 'var(--text-main)' }}>
                                {timetable.length ? Math.round((completedCount / timetable.length) * 100) : 0}%
                            </span>
                        </div>
                    </div>
                </div>
            </div>
        </Layout>
    );
};

export default Dashboard;
