import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import Modal from '../components/Modal';
import api from '../services/api';
import {
    Plus,
    Clock,
    Edit2,
    Trash2,
    Loader2,
    Calendar as CalendarIcon
} from 'lucide-react';
import toast from 'react-hot-toast';

const Timetable = () => {
    const [timetable, setTimetable] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingEntry, setEditingEntry] = useState(null);
    const [filterDay, setFilterDay] = useState('All');

    const [formData, setFormData] = useState({
        subject: '',
        day: 'Monday',
        start_time: '',
        end_time: '',
        location: '',
        priority: 'Medium',
        color: '#3b82f6',
        repeat_config: {}
    });

    const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

    const fetchData = async () => {
        try {
            setLoading(true);
            const res = await api.get('/timetable');
            setTimetable(res.data);
        } catch (error) {
            toast.error('Failed to load timetable');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const handleOpenModal = (entry = null) => {
        if (entry) {
            setEditingEntry(entry);
            setFormData({
                ...entry,
                start_time: entry.start_time.substring(0, 5),
                end_time: entry.end_time.substring(0, 5)
            });
        } else {
            setEditingEntry(null);
            setFormData({
                subject: '',
                day: 'Monday',
                start_time: '',
                end_time: '',
                location: '',
                priority: 'Medium',
                color: '#3b82f6',
                repeat_config: {}
            });
        }
        setIsModalOpen(true);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingEntry) {
                await api.put(`/timetable/${editingEntry.id}`, formData);
                toast.success('Entry updated');
            } else {
                await api.post('/timetable', formData);
                toast.success('Entry added');
            }
            setIsModalOpen(false);
            fetchData();
        } catch (error) {
            toast.error('Failed to save entry');
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this entry?')) {
            try {
                await api.delete(`/timetable/${id}`);
                toast.success('Entry deleted');
                fetchData();
            } catch (error) {
                toast.error('Failed to delete entry');
            }
        }
    };

    const filteredTimetable = filterDay === 'All'
        ? timetable
        : timetable.filter(item => item.day === filterDay);

    return (
        <Layout>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
                <div>
                    <h2 style={{ fontSize: '28px', fontWeight: '800', marginBottom: '8px' }}>Manage Timetable</h2>
                    <p style={{ color: 'var(--text-muted)' }}>Customize your weekly schedule and tasks.</p>
                </div>
                <button
                    onClick={() => handleOpenModal()}
                    className="btn-primary"
                    style={{ display: 'flex', alignItems: 'center', gap: '8px', padding: '12px 24px' }}
                >
                    <Plus size={20} /> Add Entry
                </button>
            </div>

            <div style={{
                display: 'flex',
                gap: '20px',
                marginBottom: '30px',
                overflowX: 'auto',
                paddingBottom: '10px'
            }}>
                <button
                    onClick={() => setFilterDay('All')}
                    style={{
                        padding: '10px 20px',
                        borderRadius: '20px',
                        backgroundColor: filterDay === 'All' ? 'var(--primary)' : 'var(--bg-card)',
                        color: filterDay === 'All' ? 'white' : 'var(--text-main)',
                        fontWeight: '600',
                        whiteSpace: 'nowrap'
                    }}
                >
                    All Days
                </button>
                {days.map(day => (
                    <button
                        key={day}
                        onClick={() => setFilterDay(day)}
                        style={{
                            padding: '10px 20px',
                            borderRadius: '20px',
                            backgroundColor: filterDay === day ? 'var(--primary)' : 'var(--bg-card)',
                            color: filterDay === day ? 'white' : 'var(--text-main)',
                            fontWeight: '600',
                            whiteSpace: 'nowrap'
                        }}
                    >
                        {day}
                    </button>
                ))}
            </div>

            {loading ? (
                <div style={{ display: 'flex', justifyContent: 'center', padding: '100px' }}>
                    <Loader2 className="animate-spin" size={40} color="var(--primary)" />
                </div>
            ) : (
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '24px' }}>
                    {filteredTimetable.length === 0 ? (
                        <div style={{ gridColumn: '1/-1', textAlign: 'center', padding: '60px', color: 'var(--text-muted)' }}>
                            <CalendarIcon size={48} style={{ marginBottom: '15px', opacity: 0.3 }} />
                            <p>No entries found for {filterDay === 'All' ? 'any day' : filterDay}.</p>
                        </div>
                    ) : (
                        filteredTimetable.map(item => (
                            <div key={item.id} className="card" style={{ borderTop: `6px solid ${item.color}` }}>
                                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
                                    <div style={{
                                        padding: '4px 10px',
                                        borderRadius: '8px',
                                        fontSize: '12px',
                                        fontWeight: '700',
                                        backgroundColor: 'rgba(59, 130, 246, 0.1)',
                                        color: 'var(--primary)'
                                    }}>
                                        {item.day}
                                    </div>
                                    <div style={{ display: 'flex', gap: '10px' }}>
                                        <button onClick={() => handleOpenModal(item)} style={{ background: 'none', color: 'var(--text-muted)' }}><Edit2 size={16} /></button>
                                        <button onClick={() => handleDelete(item.id)} style={{ background: 'none', color: '#ef4444' }}><Trash2 size={16} /></button>
                                    </div>
                                </div>

                                <h3 style={{ fontSize: '18px', fontWeight: '800', marginBottom: '5px' }}>{item.subject}</h3>
                                <div style={{ display: 'flex', alignItems: 'center', gap: '8px', color: 'var(--text-muted)', fontSize: '14px', marginBottom: '15px' }}>
                                    <Clock size={14} />
                                    {item.start_time.substring(0, 5)} - {item.end_time.substring(0, 5)}
                                </div>

                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                    <span style={{ fontSize: '13px', color: 'var(--text-muted)' }}>{item.location}</span>
                                    <span style={{
                                        fontSize: '11px',
                                        fontWeight: '800',
                                        textTransform: 'uppercase',
                                        color: item.priority === 'High' ? '#ef4444' : item.priority === 'Medium' ? '#f59e0b' : '#10b981'
                                    }}>
                                        {item.priority} Priority
                                    </span>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            )}

            {/* Entry Modal */}
            <Modal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                title={editingEntry ? 'Edit Entry' : 'Add New Entry'}
            >
                <form onSubmit={handleSubmit}>
                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Subject Name</label>
                        <input
                            type="text"
                            style={{ width: '100%' }}
                            value={formData.subject}
                            onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                            required
                        />
                    </div>

                    <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Day</label>
                            <select
                                style={{ width: '100%' }}
                                value={formData.day}
                                onChange={(e) => setFormData({ ...formData, day: e.target.value })}
                            >
                                {days.map(d => <option key={d} value={d}>{d}</option>)}
                            </select>
                        </div>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Priority</label>
                            <select
                                style={{ width: '100%' }}
                                value={formData.priority}
                                onChange={(e) => setFormData({ ...formData, priority: e.target.value })}
                            >
                                <option value="Low">Low</option>
                                <option value="Medium">Medium</option>
                                <option value="High">High</option>
                            </select>
                        </div>
                    </div>

                    <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Start Time</label>
                            <input
                                type="time"
                                style={{ width: '100%' }}
                                value={formData.start_time}
                                onChange={(e) => setFormData({ ...formData, start_time: e.target.value })}
                                required
                            />
                        </div>
                        <div style={{ flex: 1 }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>End Time</label>
                            <input
                                type="time"
                                style={{ width: '100%' }}
                                value={formData.end_time}
                                onChange={(e) => setFormData({ ...formData, end_time: e.target.value })}
                                required
                            />
                        </div>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Location</label>
                        <input
                            type="text"
                            style={{ width: '100%' }}
                            value={formData.location}
                            onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                        />
                    </div>

                    <div style={{ marginBottom: '25px' }}>
                        <label style={{ display: 'block', marginBottom: '5px', fontWeight: '600', fontSize: '14px' }}>Entry Color</label>
                        <div style={{ display: 'flex', gap: '10px' }}>
                            {['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899'].map(c => (
                                <div
                                    key={c}
                                    onClick={() => setFormData({ ...formData, color: c })}
                                    style={{
                                        width: '30px',
                                        height: '30px',
                                        borderRadius: '50%',
                                        backgroundColor: c,
                                        cursor: 'pointer',
                                        border: formData.color === c ? '3px solid var(--text-main)' : 'none'
                                    }}
                                ></div>
                            ))}
                        </div>
                    </div>

                    <button type="submit" className="btn-primary" style={{ width: '100%', padding: '12px' }}>
                        {editingEntry ? 'Update Entry' : 'Create Entry'}
                    </button>
                </form>
            </Modal>

            <style>{`
        .animate-spin {
          animation: spin 1s linear infinite;
        }
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
      `}</style>
        </Layout>
    );
};

export default Timetable;
